	@Override
	public ActionForward update(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response,
			ActionErrors errors, ActionMessages messages) throws Exception,
			ServletException {
		// TODO Auto-generated method stub
		AprReqRecursoForm aprReqRecursoForm = (AprReqRecursoForm) form;
		ActSolicitudDAO actSolicitudDAO = new ActSolicitudDAO();
		ActSolicitud actTemp = new ActSolicitud();

		HttpSession session = request.getSession(true);
		String target = KEY_SUCCESS;

		try {
			aprReqRecursoForm.validate(mapping, request, errors);
			if (errors.isEmpty()) {

				actTemp = actSolicitudDAO.findById(aprReqRecursoForm
						.getActSolicitud().getId());
				aprReqRecursoForm.getActSolicitud().setFechaCreacion(
						actTemp.getFechaCreacion());
				actSolicitudDAO.merge(aprReqRecursoForm.getActSolicitud());
				HibernateSessionFactory.getSession().beginTransaction()
						.commit();
				errors.add("requisicion.mensaje.exito", new ActionError(
						"requisicion.mensaje.exito.update"));
			} else {
				target = "updateFail";
				setFLAG_UPDATE(true);
			}
		} catch (Exception e) {
			e.printStackTrace();
			HibernateSessionFactory.getSession().beginTransaction().rollback();
			// TODO: handle exception
		} finally {
			HibernateSessionFactory.getSession().close();
		}

		find(mapping, form, request, response);
		return mapping.findForward(target);
	}

